bruto = 0.0
descuentos = 0.0

for line in payslip.line_ids:
    if line.code == 'BRUTO':
        bruto = line.total
    # Descuentos legales trabajador
    if line.code in ('AFP', 'SIS', 'SALUD', 'SC_TRAB'):
        descuentos += line.total  # son negativos

# Neto = bruto + descuentos (negativos)
result = bruto + descuentos
